jQuery(document).ready(function(a) {
    a(".content-background").height(a(window).height() - 40), a(".map-canvas").each(function() {
        var b = new GMaps({
            div: a(this).attr("id"),
            lat: a(this).data("latitude"),
            lng: a(this).data("longitude")
        });
        b.addMarker({
            lat: a(this).data("latitude"),
            lng: a(this).data("longitude"),
            title: a(this).data("markertitle"),
            infoWindow: {
                content: "<p>" + a(this).data("infotitle") + "</p>"
            }
        });
    });
});